export interface QuestionGroup {
  group_id: number;
  part_id: number;
  paragraph: string | null;
  transcript: string | null;
  questions: string[];
  image_url: string | string[] | null;
  audio_url?: string;
}

export interface Question {
  id: string;
  text: string;
  options: string[];
  correct_answer: string;
  explanation?: string;
}

// Helper function to find group by question ID or group ID
export const findQuestionGroup = (groupId?: number): QuestionGroup | null => {
  if (groupId) {
    return questionsData.groups.find((group) => group.group_id === groupId) || null;
  }
  return null;
};

// Helper function to get part name
export const getPartName = (partId: number): string => {
  const partNames = {
    1: 'Photographs',
    2: 'Question-Response',
    3: 'Conversations',
    4: 'Talks',
    5: 'Incomplete Sentences',
    6: 'Text Completion',
  };
  return partNames[partId as keyof typeof partNames] || `Part ${partId}`;
};
